INSERT INTO events(out_id, title, descr, categories, coords, url, start_date, end_date) VALUES (155657, '«Мафия»\ в\ антикафе\ «Совушки»', '<p>В\ последнее\ время\ мафия\ совсем\ потеряла\ стыд\.\ Пора\ городу\ что-то\ с\ этим\ сделать!</p>\n', 'games', PointFromText('POINT(56.842339 60.614893)'), 'https://kudago\.com/ekb/event/igra-mafiya-155657/', FROM_UNIXTIME(-62135433000), FROM_UNIXTIME(253370754000));INSERT INTO events(out_id, title, descr, categories, coords, url, start_date, end_date) VALUES (178761, 'музыкальный\ фестиваль\ ZRAVE', '<p>Безумные\ танцы\ всю\ ночь,\ десять\ интерактивных\ площадок,\ две\ сцены\ с отличным\ звуком,\ именитые\ диджеи —\ на первом\ музыкальном\ фестивале\ этого\ лета\ ZRAVE\ будет\ жарко!</p>', 'concert, festival', PointFromText('POINT(56.764699 60.751104)'), 'https://kudago\.com/ekb/event/festival-zrave/', FROM_UNIXTIME(1559404800), FROM_UNIXTIME(1559430000));INSERT INTO events(out_id, title, descr, categories, coords, url, start_date, end_date) VALUES (172745, 'выставка\ живописи\ и\ графики\ Марины\ Кутявиной\ «Где-нибудь\ нигде\.\.\.»', '<p>Персональная\ выставка\ Марины\ Кутявиной\ —\ это\ эмоциональное\ путешествие\ по\ удивительному\ миру\ невероятно\ трогательных\ образов\ родом\ из\ детства\ и\ глубин\ нашего\ сентиментального\ Я,\ которое\ позволит\ увидеть\ сказочное\ в\ обыденном\ и\ по-новому\ взглянуть\ на\ привычное\ бытовое\.\ На\ выставке\ представлено\ более\ 100\ работ\ художницы\.</p>', 'exhibition, other', PointFromText('POINT(56.8313807999999 60.6039650000003)'), 'https://kudago\.com/ekb/event/vyistavka-gde-nibud-nigde/', FROM_UNIXTIME(-62135433000), FROM_UNIXTIME(253370754000));INSERT INTO events(out_id, title, descr, categories, coords, url, start_date, end_date) VALUES (105620, 'проект\ «История\ в\ 3D»', '<p>Захватывающий\ фильм\ об\ истории\ города,\ об\ основных\ этапах\ развития\ Екатеринбурга,\ о\ его\ достижениях\ и\ о\ людях,\ которые\ здесь\ жили\ и\ прославляли\ родную\ землю\.</p>\n', 'exhibition, permanent-exhibitions', PointFromText('POINT(56.840641 60.611443)'), 'https://kudago\.com/ekb/event/stereo-film-istoriya-v-3d/', FROM_UNIXTIME(-62135433000), FROM_UNIXTIME(253370754000));INSERT INTO events(out_id, title, descr, categories, coords, url, start_date, end_date) VALUES (6013, 'выставка\ «Кабинет\ редкостей»', '<p>Это\ настоящая\ кунсткамера,\ экспонаты\ для\ которой\ тщательно\ собирались\ с\ 1870\ года\.</p>\n', 'exhibition, permanent-exhibitions', PointFromText('POINT(56.837263 60.605667)'), 'https://kudago\.com/ekb/event/vystavka-kabinet-redkostej/', FROM_UNIXTIME(1366693200), FROM_UNIXTIME(1366693200));INSERT INTO events(out_id, title, descr, categories, coords, url, start_date, end_date) VALUES (67191, '«Мафия»\ в\ «Коммуникаторе»', '<p>Еженедельные\ «бандитские»\ разборки\ понарошку\.</p>\n', 'games', PointFromText('POINT(56.843481 60.614704)'), 'https://kudago\.com/ekb/event/igra-v-mafiyu-v-kommunikatore/', FROM_UNIXTIME(1440428400), FROM_UNIXTIME(1440442740));INSERT INTO events(out_id, title, descr, categories, coords, url, start_date, end_date) VALUES (155957, 'настольные\ игры\ на\ английском\ языке', '<p>Наконец-то\ можно\ играть\ и\ учиться\ одновременно\.</p>\n', 'games', PointFromText('POINT(56.8392044999998 60.6145861000003)'), 'https://kudago\.com/ekb/event/igra-nastolnyie-igryi-na-anglijskom-yazyike/', FROM_UNIXTIME(-62135433000), FROM_UNIXTIME(253370754000));INSERT INTO events(out_id, title, descr, categories, coords, url, start_date, end_date) VALUES (106303, 'выставка\ «Возникновение\ и\ развитие\ Уральских\ гор»', '<p>Выставка\ покорит\ количеством\ экспонатов\ и\ полнотой\ раскрытия\ темы\ появления\ и\ развития\ жизни\ на\ Земле,\ формирования\ Уральских\ гор\ и\ всего\ близлежащего\ региона\.</p>\n', 'exhibition, permanent-exhibitions', PointFromText('POINT(56.837263 60.605667)'), 'https://kudago\.com/ekb/event/vystavka-vozniknovenie-i-razvitie-uralskih-gor/', FROM_UNIXTIME(-62135433000), FROM_UNIXTIME(253370754000));INSERT INTO events(out_id, title, descr, categories, coords, url, start_date, end_date) VALUES (159096, 'выставка\ «Изумрудная\ комната»', '<p>На\ уникальной\ экспозиции\ ювелирных\ изделий\ и\ драгоценных\ камней\ вы\ сможете\ полюбоваться\ редкими\ образцами\ изумрудов,\ аквамаринов,\ бериллов\ и\ александритов\.</p>\n', 'exhibition', PointFromText('POINT(56.839119 60.606934)'), 'https://kudago\.com/ekb/event/vyistavka-izumrudnaya-komnata/', FROM_UNIXTIME(1499367600), FROM_UNIXTIME(253370754000));INSERT INTO events(out_id, title, descr, categories, coords, url, start_date, end_date) VALUES (149215, 'выставка\ «Жизнь\ и\ творчество\ Эрнста\ Неизвестного»', '<p>Уникальное\ собрание\ живописи,\ скульптуры\ и\ графики\ знаменитого\ свердловского\ художника\.</p>\n', 'exhibition', PointFromText('POINT(56.832063 60.603116)'), 'https://kudago\.com/ekb/event/vystavka-zhizn-i-tvorchestvo-ehrnsta-neizvestnogo/', FROM_UNIXTIME(-62135433000), FROM_UNIXTIME(253370754000));INSERT INTO events(out_id, title, descr, categories, coords, url, start_date, end_date) VALUES (67332, '«Мафия»\ в\ «Автографе»', '<p>«Мафия»\ —\ настолько\ увлекательная,\ интересная\ и\ популярная\ игра,\ что\ в\ объяснении\ правил,\ наверное,\ не\ нуждается\.\ Все\ знают\ знаменитую\ фразу\ «город\ засыпает,\ просыпается\ мафия»,\ и\ антикафе\ «Автограф»\ приглашает\ весь\ город\ к\ себе,\ чтобы\ эту\ самую\ мафию\ вычислить\.</p>\n', 'games', PointFromText('POINT(56.8327299499218 60.6036704694318)'), 'https://kudago\.com/ekb/event/igra-v-mafiyu-v-antikafe-avtograf/', FROM_UNIXTIME(1438444800), FROM_UNIXTIME(1438455540));INSERT INTO events(out_id, title, descr, categories, coords, url, start_date, end_date) VALUES (155946, 'художественная\ мастерская\ в\ «Коммуникаторе»', '<p>Здесь\ вы\ на\ собственном\ опыте\ убедитесь:\ кисточка\ и\ карандаш\ способны\ творить\ настоящие\ чудеса\.</p>\n', 'education', PointFromText('POINT(56.843481 60.614704)'), 'https://kudago\.com/ekb/event/znaniya-hudozhestvennaya-masterskaya-v-kommunikatore/', FROM_UNIXTIME(-62135433000), FROM_UNIXTIME(253370754000));INSERT INTO events(out_id, title, descr, categories, coords, url, start_date, end_date) VALUES (105609, 'экспозиция\ «Западноевропейское\ искусство\ XIV-XIX\ веков»', '<p>Выставка\ посвящена\ работам\ талантливых\ художников,\ которые\ творили\ в\ европейских\ странах\ столетия\ назад\.\ Практически\ все\ экспонаты\ ранее\ входили\ в\ частную\ коллекцию\ К\.\ Клопотова\ и\ были\ очень\ высоко\ оценены\ экспертами\.\ Теперь\ все\ собранные\ шедевры\ сможет\ увидеть\ каждый\.</p>\n', 'exhibition, permanent-exhibitions', PointFromText('POINT(56.83517 60.603457)'), 'https://kudago\.com/ekb/event/ekspoziciya-zapadnoevropejskoe-iskusstvo-xiv-xix-v/', FROM_UNIXTIME(-62135433000), FROM_UNIXTIME(253370754000));INSERT INTO events(out_id, title, descr, categories, coords, url, start_date, end_date) VALUES (106300, 'выставка\ «Насекомые\ Свердловской\ области»', '<p>Сколько\ видов\ насекомых\ вы\ знаете\ или\ когда-либо\ видели\?\ Даже\ самые\ внимательные\ смогут\ насчитать\ три-четыре\ десятка,\ может,\ чуть\ больше\.\ Но,\ на\ самом\ деле,\ разных\ насекомых\ в\ Свердловской\ области\ несколько\ сотен\ и\ даже\ тысяч,\ и\ посмотреть\ на\ них\ можно\ будет\ в\ ходе\ выставки\.</p>\n', 'exhibition, permanent-exhibitions', PointFromText('POINT(56.837263 60.605667)'), 'https://kudago\.com/ekb/event/vystavka-nasekomye-sverdlovskoj-oblasti/', FROM_UNIXTIME(-62135433000), FROM_UNIXTIME(253370754000));INSERT INTO events(out_id, title, descr, categories, coords, url, start_date, end_date) VALUES (91316, 'экономическая\ игра\ «Денежный\ поток»\ в\ пространстве\ «Автограф»', '<p>Увлекательная\ настольная\ игра\ «Денежный\ поток»\ не\ только\ прекрасный\ способ\ развлечься\ в\ дружеской\ компании,\ но\ и\ уникальный\ образовательный\ процесс,\ который\ имитирует\ реальную\ финансовую\ деятельность\ и\ учит\ игроков\ зарабатывать\ настоящие\ деньги\.</p>\n', 'games', PointFromText('POINT(56.8327299499218 60.6036704694318)'), 'https://kudago\.com/ekb/event/ekonomicheskaya-igra-denezhnyj-potok-v-prostranstv/', FROM_UNIXTIME(-62135433000), FROM_UNIXTIME(253370754000));INSERT INTO events(out_id, title, descr, categories, coords, url, start_date, end_date) VALUES (71476, 'клуб\ разговорного\ английского\ Bright', '<p>Нескучные\ уроки\ разговорного\ английского\.</p>\n', 'education', PointFromText('POINT(56.817824 60.612728)'), 'https://kudago\.com/ekb/event/klub-razgovornogo-anglijskogo-bright/', FROM_UNIXTIME(1440925200), FROM_UNIXTIME(1440932400));INSERT INTO events(out_id, title, descr, categories, coords, url, start_date, end_date) VALUES (97416, 'тренинг\ по\ театральной\ импровизации', '<p>Здесь\ будет\ интересно\ всем,\ кто\ хочет\ чувствовать\ себя\ перед\ публикой\ уверенно:\ и\ неважно,\ какое\ выступление\ предстоит\ –\ сложный\ доклад\ или\ ведение\ дружеской\ вечеринки,\ –\ вы\ научитесь\ привлекать\ к\ себе\ внимание\.</p>\n', 'education, games', PointFromText('POINT(56.8327299499218 60.6036704694318)'), 'https://kudago\.com/ekb/event/trening-po-teatralnoj-improvizacii/', FROM_UNIXTIME(1444849200), FROM_UNIXTIME(253370754000));INSERT INTO events(out_id, title, descr, categories, coords, url, start_date, end_date) VALUES (105608, 'выставка\ «Русское\ искусство\ XVIII–ХIХ\ вв\.»', '<p>В\ начале\ XVIII\ века\ в\ России\ появляется\ искусство\ масляной\ живописи\.\ О\ том,\ как\ это\ повлияло\ на\ дальнейшее\ развитие\ художественной\ культуры\ страны,\ и\ расскажет\ выставка\.</p>\n', 'exhibition', PointFromText('POINT(56.83517 60.603457)'), 'https://kudago\.com/ekb/event/ekspoziciya-russkoe-iskusstvo-xvii-nachala-xxvv/', FROM_UNIXTIME(-62135433000), FROM_UNIXTIME(253370754000));INSERT INTO events(out_id, title, descr, categories, coords, url, start_date, end_date) VALUES (155945, 'игротека\ в\ «Коммуникаторе»', '<p>Ловите\ рыбку,\ сражайтесь\ с\ монстрами\ и\ стройте\ колонии\ в\ удивительных\ мирах\ настольных\ игр\.</p>\n', 'games', PointFromText('POINT(56.843481 